import React, { useContext, useEffect } from 'react'

import BodyContent from './Components/BodyContent'
import MainArea from './Components/MainArea'

import web3 from './Connection/web3'
import Web3Context from './Store/web3-context'
import CollectionContext from './Store/collection-context'

import { nftAddress } from './Constants/address'
import MyNFT from './Abis/MyNFT'

const App = () => {
  const web3Ctx = useContext(Web3Context)
  const collectionCtx = useContext(CollectionContext)
  //내가 만든 Contract 추가
  // const fundingCtx = useContext(FundingContext)
  useEffect(() => {
    // Check if the user has Metamask active
    if (!web3) {
      window.alert(
        'Non-Ethereum browser detected. You should consider trying MetaMask!',
      )
      return
    }

    // Function to fetch all the blockchain data
    const loadBlockchainData = async () => {
      // Request accounts acccess if needed
      try {
        await window.ethereum.request({ method: 'eth_requestAccounts' })
      } catch (error) {
        console.error(error)
      }

      // Load account
      const accounts = await web3Ctx.loadAccount(web3)
      // Load Network ID
      const networkId = await web3Ctx.loadNetworkId(web3)
      console.log(accounts)
      console.log(networkId)

      const collectionContract = collectionCtx.loadContract(
        web3,
        MyNFT.abi,
        nftAddress,
      )

      //mint 버튼이 떠야함 -> main.js에서 MintForm이 떠야하는데 그러려면 아래 작업을 해주어야함
      if (collectionContract) {
        console.log('Collection contract deployed to ropsten detwork')

        console.log('collectionContract address', collectionContract) //address 어떻게 찍나?
        let totalSupply = await collectionCtx.loadTotalSupply(
          collectionContract,
        )
        collectionCtx.setNftIsLoading(false) // 이렇게 호출하는게 맞나?
        collectionCtx.loadCollection(collectionContract, totalSupply)
      } else {
        window.alert(
          'NFTMarketplace contract not deployed to detected network.',
        )
      }

      // Metamask Event Subscription - Account changed
      window.ethereum.on('accountsChanged', (accounts) => {
        web3Ctx.loadAccount(web3)
      })

      // Metamask Event Subscription - Network changed
      window.ethereum.on('chainChanged', (chainId) => {
        window.location.reload()
      })
    }

    loadBlockchainData()
  }, [])
  const showContent = web3 && collectionCtx.contract && web3Ctx.account

  return (
    <div className="App">
      {showContent && <MainArea />}
      <BodyContent />
    </div>
  )
}

export default App
